const swiper = new Swiper(".swiper", {
    loop: !0,
    keyboard: { enabled: !0, onlyInViewport: !1 },
    navigation: {
      nextEl: ".slider-button--next",
      prevEl: ".slider-button--prev",
    },
  }),
  reviewsSlider = new Swiper(".reviews-slider", {
    loop: !0,
    navigation: {
      nextEl: ".reviews-slider__button--next",
      prevEl: ".reviews-slider__button--prev",
    },
  });
$(".parallax-window").parallax({ imageSrc: "img/newsletter.jpg" }),
  jQuery(window).trigger("resize").trigger("scroll"),
  $(document).ready(function () {
    $("#burger").click((e) => {
      $(".menu-button").toggleClass("menu-button--active"),
        $(".menu-button__line").toggleClass("menu-button__line--active"),
        $(".navbar-bottom").toggleClass("navbar-bottom--visible");
    });
    let e = $("[data-toggle=modal]");
    const i = $(".modal__close"),
      o = $(".modal__wrapper"),
      r = $(".modal__window");
    e.click(() => {
      o.addClass("modal__wrapper--visible"),
        r.addClass("modal__window--visible");
    }),
      i.click(() => {
        o.removeClass("modal__wrapper--visible"),
          r.removeClass("modal__window--visible");
      }),
      $(document).keydown(function (e) {
        "Escape" === e.key &&
          (o.removeClass("modal__wrapper--visible"),
          r.removeClass("modal__window--visible"));
      });
  }),
  $(".validate-form").each(function () {
    $(this).validate({
      errorClass: "invalid",
      messages: {
        name: {
          required: "Please specify your name",
          minlength: "Your name must contain at least two symbols",
        },
        phone: { required: "Entering phone-number is necessary" },
        email: { required: "We need your email to contact you" },
        msg: { required: "Describe your problem or ask question" },
      },
    }),
      $(".phone_with_ddd").mask("+7 (999) 999-99-99"),
      AOS.init();
  });
